import React, { useState } from 'react';
import './App.css';
import ProductList from './components/ProductList';
import CartPage from './components/CartPage';

function App() {
  const [cartItems, setCartItems] = useState([]);
  const [showCart, setShowCart] = useState(false);

  const addToCart = (product) => {
    setCartItems([...cartItems, product]);
  };

  return (
    <div className="App">
      <nav className="navbar">
        <h2 style={{color: '#b8860b', margin: 0}}>NAKSH JEWELS</h2>
        <button className="cart-button" onClick={() => setShowCart(!showCart)}>
          üõí {showCart ? 'Back to Shop' : 'View Cart'}
          {!showCart && <span className="cart-count">{cartItems.length}</span>}
        </button>
      </nav>

      <div className="container">
        {showCart ? (
          <div className="cart-view">
            <button className="back-btn" onClick={() => setShowCart(false)}>‚Üê Continue Shopping</button>
            <CartPage cartItems={cartItems} />
          </div>
        ) : (
          <ProductList addToCart={addToCart} />
        )}
      </div>
    </div>
  );
}

export default App;
