// frontend/src/App.js
import React from 'react';
import { CartProvider } from './context/CartContext';
import ProductList from './components/ProductList';
import CartPage from './components/CartPage';
import './App.css'; // Your custom CSS since UI libraries are banned 

function App() {
  return (
    <CartProvider>
      <div className="App">
        <h1>Naksh Jewels Shop</h1>
        <main>
          {/* You can use a state to toggle between these or just show both */}
          <ProductList />
          <CartPage />
        </main>
      </div>
    </CartProvider>
  );
}

export default App;
