import React from 'react';

const CartPage = ({ cartItems }) => {
  const total = cartItems.reduce((sum, item) => sum + item.price, 0);

  return (
    <div>
      <h2>Your Luxury Selection</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty. Start adding some sparkles!</p>
      ) : (
        <>
          {cartItems.map((item, index) => (
            <div key={index} className="cart-item" style={{display: 'flex', justifyContent: 'space-between', padding: '10px 0', borderBottom: '1px solid #eee'}}>
              <span>{item.name}</span>
              <strong>₹{item.price.toLocaleString('en-IN')}</strong>
            </div>
          ))}
          <div style={{marginTop: '20px', textAlign: 'right', fontSize: '1.5rem'}}>
            <strong>Total: ₹{total.toLocaleString('en-IN')}</strong>
          </div>
        </>
      )}
    </div>
  );
};

export default CartPage;
